                        versedroid<span className="text-vd-accent">.</span>
                    </h1>
                    <p className="mt-2 text-sm text-vd-text-secondary">
                        Admin Dashboard
                    </p>
                </div>

                {/* Login card */}
                <form
                    onSubmit={handleLogin}
                    className="rounded-xl border border-vd-border bg-vd-bg-secondary p-6 space-y-4"
                >
                    {error && (
                        <div className="flex items-center gap-2 rounded-lg border border-red-500/30 bg-red-500/10 p-3 text-sm text-red-400">
                            <AlertCircle className="h-4 w-4 shrink-0" />
                            {error}
                        </div>
                    )}

                    <div className="space-y-2">
                        <Label htmlFor="email">Email</Label>
                        <Input
                            id="email"
                            type="email"
                            placeholder="admin@example.com"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            autoComplete="email"
                        />
                    </div>

                    <div className="space-y-2">
                        <Label htmlFor="password">Password</Label>
                        <Input
                            id="password"
                            type="password"
                            placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            autoComplete="current-password"
                        />
                    </div>

                    <Button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-vd-accent text-vd-bg-primary hover:bg-vd-accent/90"
                    >
                        {loading ? (
                            <>
                                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                Signing in...
                            </>
                        ) : (
                            "Sign In"
                        )}
                    </Button>
                </form>

                <p className="text-center text-xs text-vd-text-secondary">
                    <a href="/" className="hover:text-vd-accent transition-colors">
                        ‚Üê Back to site
                    </a>
                </p>
            </div>
        </div>
    );
    
}

